function getOuterHTML(a) {
	var b = "";
	switch (a.nodeType) {
	case 1:
		b = "<" + a.nodeName;
		var c = "";
		for (var d = 0; d < a.attributes.length; d++) {
			if (a.attributes[d].nodeName.substr(0, 4) == "_moz") continue;
			if (a.attributes[d].nodeValue.substr(0, 4) == "_moz") continue;
			if (a.nodeName == 'TEXTAREA' && a.attributes[d].nodeName.toLowerCase() == 'value') {
				c = a.attributes[d].nodeValue
			} else {
				b += ' ' + a.attributes[d].nodeName + '="' + a.attributes[d].nodeValue.replace(/"/gi, "'") + '"'
			}
		}
		b += '>';
		if (a.nodeName == 'TEXTAREA') {
			b += c
		} else if (a.nodeName == 'OBJECT') {
			var e;
			for (var i = 0; i < a.childNodes.length; i++) {
				e = a.childNodes[i];
				if (e.nodeType == 1) {
					if (e.tagName == "PARAM") {
						b += "<param name=\"" + e.name + "\" value=\"" + e.value.replace(/"/gi, "'") + "\"/>\n"
					} else if (e.tagName == "EMBED") {
						b += getOuterHTML(e)
					}
				}
			}
		} else {
			b += a.innerHTML
		}
		b += "</" + a.nodeName + ">";
		break;
	case 8:
		b = "<!" + "--" + a.nodeValue + "--" + ">";
		break;
	case 3:
		b = a.nodeValue;
		break
	}
	return b
};